import Input from "./FORM/Input.js"
import Button from "./FORM/Button.js"
import { Fragment, useState } from "react"

const TodoInput = () => {

  const [inputTask, setInputTask] = useState('');
  const [list,setList] = useState([])
  const [editing,setEditing] = useState(false)
  const [newItem, setNewItem] = useState(inputTask)
  // console.log(list)
  const handleChangeInput = (event) => {
    setInputTask(event.target.value)
  }

  const handleAddTodo = () => {
    const newTask = {
        id: Math.floor(Math.random() * 10),
        todo: inputTask
    };

    setList([...list, newTask])    
    setInputTask('') // clear input fiel when user clicked button.
  } 

  const handleDeleteTask = (id) => {
    const newList = list.filter((todo) => todo.id !== id)
    setList(newList)
  }

  const handleEditing = () => {
    setEditing(!editing)
  }


  const handleSaveEdit = (e) => {
    const newEdit = {
      id: Math.floor(Math.random() * 10),
      todo: setNewItem(e.target.value)
    }
    setList([...list,newEdit])
  }


  return (  
    <Fragment>   
      <div className="container">
        <h2>My to do list</h2>
        <Input 
        type="text"
        placeholder= {editing ? "Edit Task" : "Add Task"}
        // value={inputTask}
        value={editing ? newItem : inputTask}
        // onChange = {handleChangeInput}
        onChange = {editing ? (e) => setNewItem(e.target.value) : handleChangeInput}
        ></Input>
        <Button 
        className = "button-btn"
        onClick = {handleAddTodo}
        >{editing ? "Edit Task" : "Add Task"}</Button>

              <ul>
            {list.map((todo,index) => (
                <li key={index} className="task">{todo.todo}
                <Button className="delete-button" onClick={() => handleDeleteTask(todo.id)}>Delete</Button>
                <Button className="edit-button" onClick={handleEditing}>Edit</Button>

                </li>
            ))}
        </ul>
            </div>
    </Fragment>

  )
}
export default TodoInput;
