import Input from "./FORM/Input.js"
import Button from "./FORM/Button.js"
import { useState } from "react"


export default function Todoinput() {

    const [inputTask, setInputTask] = useState("")

    // // Step 1: trigger an event when we type a task into the input box
    // const handleInputChange = (event) => {
    //     setInputTask(event.target.value) // access the value of revent's target
    // }

    // Step 2: 
    const [list, setList] = useState([]);

    const handleAddToDo = () => {

        setList([...list,inputTask]) // spread operator [...list] to create a new array with the existing taks in the list. Append the new Task to the end of the array
         setInputTask("") // clear the input field when the user clisks the button
    }

    return (
        <div className="appToDo">
        <h1>Add list to do you will do in the day</h1>
        <form className="input-section">
            <Input
            id="todoInput"
            type="text"
            placeholder = "Add item..."
            value={inputTask}
            onChange={() => {setInputTask(event.target.value)}}
            />

            <Button 
            id="addBtn"
            // type="submit"
            className="add"
            onClick = {handleAddToDo}
            >Add</Button>
        </form>
        <ul>
            {list.map((todo) => {
                <li className="task" key={todo.id}>
                    {todo}
                </li>
            })}
        </ul>
        </div>
    )
}
