import Input from "./FORM/Input.js"
import Button from "./FORM/Button.js"
import { useState } from "react"
import Todo from './Todo.js';

const TodoInput = () => {

    const [todo, setTodo] = useState('')

    const handleUpdateTodo = (event) => {
       const newTodo = event.target.value;
       setTodo(newTodo);
    }

    const handleAddTodo = (todo) => {
        const [list,setList] = useState([])

        const newTask = {
            id: Math.random, // create id of task
            todo: todo // new task entered by user
        }

        setList([...list,newTask]) // update the list state by using the spread operator [...list] => create a new array which the existing task in the lisk. Append the newTask to the end of the array.
        setTodo('') // reset the todo and clearing the input field when the user clicks the button
    }

    return (
       <div className="container">
        <h2>Add To do List</h2>
        <Input 
        type="text"
        placeholder = "Add item"
        value = {todo}
        onChange = {handleUpdateTodo}
        ></Input>

        <Button className="button" onClick>
            Add
        </Button>
       </div>
    )
}
export default TodoInput;
