import Input from "./FORM/Input.js"
import Button from "./FORM/Button.js"
import { useState } from "react"

const TodoInput = () => {

    const [inputTask, setInputTask] = useState('');
    const [list,setList] = useState([]);


    const handleUpdateTodo = (event) => {
       const newTodo = event.target.value;
       setInputTask(newTodo);
    }

    const handleAddTodo = () => {

        const newTask = {
            id: Math.random, // create id of task
            setInputTask: inputTask // new task entered by user
        }

        setList([...list,newTask]) // update the list state by using the spread operator [...list] => create a new array which the existing task in the lisk. Append the newTask to the end of the array.
        setInputTask('') // reset the todo and clearing the input field when the user clicks the button
    }

    return (
       <div className="container">
        <h2>Add To do List</h2>
        <Input 
        type="text"
        placeholder = "Add item"
        value = {inputTask}
        onChange = {handleUpdateTodo}
        ></Input>

        {/* <Button className="button" onClick={handleAddTodo(inputTask)}>
            Add
        </Button> */}

        {/* <ul>
            {list.map((todo) => (
                <li className="task" key={todo.id}>{todo.todo}</li>
            ))}
        </ul> */}
       </div>
    )
}
export default TodoInput;
