import Input from "./FORM/Input.js"
import Button from "./FORM/Button.js"
import { Fragment, useState } from "react"

const TodoInput = () => {

  const [inputTask, setInputTask] = useState('');
  const [list,setList] = useState([])
  const [editTask, setEditTask] = useState({
    todolist : [],
    id: "",
    todo: "",
    editItem : false
  })
  const [test1,setTest1] = useState(false)

  // console.log(editTask)

  // console.log(list)
  const handleChangeInput = (event) => {
    setInputTask(event.target.value)
  }

  const handleAddTodo = () => {
    const newTask = {
        id: Math.floor(Math.random() * 10),
        todo: inputTask
    };

    setList([...list, newTask])    
    setInputTask('') // clear input fiel when user clicked button.
  } 

  const handleDeleteTask = (id) => {
    const newList = list.filter((todo) => todo.id !== id)
    setList(newList)
  }

  const handleEditTask = (id) => {
    const newList = list.filter((todo) => todo.id !== id)

    const selectefItem = list.find((todo) => todo.id === id) 
    // console.log(selectefItem.todo, selectefItem.id) 

    setEditTask({
      todolist:newList,
      id:selectefItem.id,
      todo:selectefItem.todo,
      editItem: true
    })

  }
  const IsEditItem = () => {
    setTest1(setEditTask.editItem)
  }

  // const IsEditItem = () => {
  //   if (setEditTask.editItem === "true") {
  //     const corrigerTach = setEditTask.todo = "Je vais corriger la tâche."
  //     console.log(corrigerTach)
  //   } else {
  //     console.log(setEditTask.todo)
  //   }
  // }


  return (  
    <Fragment>   
      <div className="container">
        <h2>My to do list</h2>
        <Input 
        type="text"
        placeholder="Add item"
        value={inputTask}
        onChange = {handleChangeInput}
        ></Input>
        <Button 
        className = "button-btn"
        onClick = {handleAddTodo}
        >{IsEditItem ? "Edit Item" : "ADD Item"}</Button>
        <ul>
            {list.map((todo,index) => (
                <li key={index} className="task">{todo.todo}
                <Button className="delete-button" onClick={() => handleDeleteTask(todo.id)}>Delete</Button>
                <Button className="edit-button" onClick={() => handleEditTask(todo.id)}>Edit</Button>
                {/* <Button className="edit-button" onClick={IsEditItem}>Check</Button> */}
                </li>
            ))}
        </ul>
    </div>
    </Fragment>

  )
}
export default TodoInput;
